function display_post_artists_shortcode() {
    // Get the terms (artists) of the "artist" taxonomy for the current post
    $artists = get_the_terms(get_the_ID(), 'artist');

    // Check if artists exist
    if ($artists && !is_wp_error($artists)) {
        // Start building the output
        $output = [];

        // Loop through each artist
        foreach ($artists as $artist) {
            // Generate a link to the artist archive
            $artist_link = get_term_link($artist);

            // Append each artist to the output
            $output[] = esc_html($artist->name);
        }
		
		// Merge the artist names with a comma separator
		$output = implode(', ', $output);

        return $output;
    }

    // Return an empty string if no artists are found
    return '';
}

// Register the shortcode
add_shortcode('post_artists', 'display_post_artists_shortcode');

add_filter( 'aioseo_schema_output', 'aioseo_schema_change_urls' );
function aioseo_schema_change_urls( $graphs ) {
    if ( false === strpos( $_SERVER['REQUEST_URI'], 'search-results/listing/' ) ) {
        return $graphs;
    }
	
    $request_uri = explode( '?', $_SERVER['REQUEST_URI'] );
	$request_uri = explode( '#', $request_uri[0] );
    $url_parts = trim($request_uri[0], '/');
    $url_parts = explode('/', $url_parts);
    $last_segment = end($url_parts);
    $old_url = get_permalink();
    $new_url     = home_url( $request_uri[0] );	
	
    foreach ( $graphs as $index => &$value ) {
        if ( is_array( $value ) ) {
            $value = aioseo_schema_change_urls( $value );
        }

        if ( is_string( $value ) ) {
            if ( $value === $old_url ) {
                $value = $new_url;
            } elseif ( false !== strpos( $value, $old_url . '#' ) ) {
                $value = str_replace( $old_url, $new_url, $value );
            }
        }
		
    }

    return $graphs;
}
